s = "aaaaaaaa"
words = ["aaa", "aaa"]

i = 0
n = len(words)
l = len(words[0])
t = n * l
result = []

while(i < len(s) - t + 1):
    word = s[i:i + t]
    split_strings = [word[index : index + l] for index in range(0, len(word), l)]
    print(split_strings)
    check = [x for x in split_strings if split_strings.count(x) == words.count(x)]
    print(check)
    if len(check) == n:
        result.append(i)
    i += 1
    
